import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const AddSalary = () => {
  const [employeeData, setEmployeeData] = useState([]);
  const productId = useParams();
  const ID = productId["id"];

  const [formData, setFormData] = useState({
    id: 0,
    amount: 0,
    description: '',
  });
  let navigate = useNavigate();
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async(e) => {
    e.preventDefault();
    // Placeholder for handling form submission
    await axios.get("http://localhost:9191/employees/"+formData.id.toString()).then(async(response) => {  
      console.log("Adding");
      console.log(response.json);
      await axios.post("http://localhost:9191/employeesalaries/", {response, amount:formData.amount, description:formData.description}).then((response1) => {  
        console.log("Adding");
        console.log(response1.json);
      navigate("/employee/"+ID.toString());
    }).catch (error => {
      console.log(error);
    })});;



    console.log('Form submitted with data:', formData);
    // You can add your logic for handling the form data here
  };

  return (
    <div>
      <h2>Expense Form</h2>
      <form onSubmit={handleSubmit}>
      <label>
          Employee ID:
          <input
            type="number"
            name="id"
            value={formData.id}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Amount:
          <input
            type="number"
            name="amount"
            value={formData.amount}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Description:
          <input
            type="text"
            name="description"
            value={formData.description}
            onChange={handleChange}
          />
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default AddSalary;
